create schema boodschappenlijstje;

create table boodschappenlijstje.productgroepen (
       id numeric,
       naam varchar(50),
       image_naam varchar(50),
       sort_order numeric
);

create table boodschappenlijstje.producten (
       id numeric,
       naam varchar(50),
       image_naam varchar(50),
       eenheid varchar(20),
       product_groep_id numeric
);

create table boodschappenlijstje.boodschappenlijst (
       id        numeric,
       productId numeric,
       aantal    numeric
);


